/**
 * Create a bridge for exposing Inferno's component tree to React DevTools.
 *
 * It creates implementations of the interfaces that ReactDOM passes to
 * devtools to enable it to query the component tree and hook into component
 * updates.
 *
 * See https://github.com/facebook/react/blob/59ff7749eda0cd858d5ee568315bcba1be75a1ca/src/renderers/dom/ReactDOM.js
 * for how ReactDOM exports its internals for use by the devtools and
 * the `attachRenderer()` function in
 * https://github.com/facebook/react-devtools/blob/e31ec5825342eda570acfc9bcb43a44258fceb28/backend/attachRenderer.js
 * for how the devtools consumes the resulting objects.
 */
export declare function createDevToolsBridge(): {
    componentAdded: (vNode: any) => void;
    componentUpdated: (vNode: any) => void;
    componentRemoved: (vNode: any) => void;
    ComponentTree: {
        getNodeFromInstance(instance: any): any;
        getClosestInstanceFromNode(dom: any): any;
    };
    Mount: {
        _instancesByReactRootID: {};
        _renderNewRootComponent(instance?: any): void;
    };
    Reconciler: {
        mountComponent(instance?: any): void;
        performUpdateIfNecessary(instance?: any): void;
        receiveComponent(instance?: any): void;
        unmountComponent(instance?: any): void;
    };
};
